$(document).ready(function(){scrolltotop_scroll_speed=400;scrolltotop_animation_speed=500;scrolltotop_fadein_start_position=200;scrolltotop_circle_mode=!0;if(scrolltotop_circle_mode==!0){$("#scrolltotop_parent").addClass("circle")}else{$("#scrolltotop_parent").addClass("square")}$("#scrolltotop_parent").on("click keypress",function(){$("html, body").animate({scrollTop:0},scrolltotop_scroll_speed);document.activeElement.blur()});$(window).scroll(function(){if(window.pageYOffset>=scrolltotop_fadein_start_position){$("#scrolltotop_parent").fadeIn(scrolltotop_animation_speed)}else{$("#scrolltotop_parent").fadeOut(scrolltotop_animation_speed)}});$(document).keydown(function(e){var key_pressed=e.keykey_pressed||e.which;if(key_pressed=="27"){if($("#scrolltotop_arrow").is(":focus")){document.activeElement.blur()}}})})